  const showCartHandler=()=>{
    setCartIsShown(true)
  }
  const hideCarthandler=()=>{
    setCartIsShown(false)
  }
  return (
    <Fragment>
      {cartIsShown && <Cart/>}
      <Header/>
      <main>
          <Meals/>
      </main>
    </Fragment>
  );
}

export default App;
